mapper.update:
UPDATE 
	EQUIPMENT 
SET 
	EQ_WEAPON = #{useItem}

	EQ_CHNAME = #{content}

		AND EQ_WEAPON = #{equipItem}

UPDATE USER_CHARACTER

	 CH_ATTACK = #{chAttack} 
	 CH_DEFENSE = CH_DEFENSE + #{chDefense} 
	 CH_HP = CH_HP + #{chHp} 
	 CH_MP = CH_MP + #{chMp} 

WHERE CH_NAME=#{chName}

mapper.select:
SELECT GR_NAME as chName, GR_CONTENT as gbReplyContent, GR_DATE as  gbWDate,
GR_CODE as grCode
FROM GBREPLY WHERE GR_BDCODE = #{gbCode}
